    function getParameterByName(name, url) {
        if (!url) url = window.location.href;
        name = name.replace(/[\[\]]/g, "\\$&");
        var regex = new RegExp("[?&]" + name + "(=([^&#]*)|&|#|$)"),
            results = regex.exec(url);
        if (!results) return null;
        if (!results[2]) return '';
        return decodeURIComponent(results[2].replace(/\+/g, " "));
    }

    function handle_previewable_click(e, doubleClick) {

        if($('#dashboard-preview').is(':visible')) {
            $(".dashboard-loader-mock, .dashboard-real").toggleClass('hidden');
        }

        if(e.closest('tr').hasClass('previewed')) {
            e.closest('tr').removeClass('previewed');
        } else {
            $('.dataTable tbody tr').removeClass('previewed');
            e.closest('tr').addClass('previewed');
        }

        if($('.dataTable tbody tr').hasClass('previewed') == false) {
            $('#dashboard-preview').hide();
            $('#dashboard-chart').removeClass('invisible');
        } else {
            $('#dashboard-preview').show();
            $('#dashboard-help').hide();
            $('#dashboard-chart').addClass('invisible');
            $('[ref=helpDashboardToggle]').removeClass('active');
            setTimeout(function(){
                $('.dashboard.dashboard-loader-mock, .dashboard-real').toggleClass('hidden');
                //construct_fake_flexlist(null, $('[ref=mainList] .flex-list-view-content'), Math.floor((Math.random()*20)+1) + 10);
                $('.dashboard-real').attr('ref-id', e.closest('tr').attr('id'));
                if(doubleClick == true)
                    e.closest('tr').addClass('previewed');
                fake_preview_render();
            }, 1250);
        }
    }

