function handle_alert(a, type, autoclose, title, message) {
    var color, icon_prefix, aid;
    aid = Math.floor(Math.random() * 20)
    if(a != true) {
        $('#'+a).remove();
        return false;
    } else {
        if(type=="success") {
            color = "green";
            icon_prefix = "fa-check-circle";
            css = "alert-success";
        } else if (type=="error") {
            color = "red";
            icon_prefix = "fa-exclamation-circle";
            css = "alert-danger";
        } else if (type=="warning") {
            color = "#e5a117";
            icon_prefix = "fa-exclamation-triangle";
            css = "alert-warning";
        } else if (type=="info") {
            icon_prefix = "fa-info-circle";
            color = "#0f7799";
            css = "alert-info";
        } else if (type=="confirm") {
            icon_prefix = "";
            color = "";
            css = "alert-warning";
        } else if (type=="loading") {
            icon_prefix = "";
            color = "";
            css = "alert-loading";
        } else {
            color = "blank";
        }

        if(type=="confirm") {
            $('<div id="a' + aid + '" class="alert-wrapper"><div class="alert-page-overlay"></div><div class="alert-confirmation"><div class="alert ' +  css + ' alert-block"><div class="indicator"><i class="fa fa-exclamation-triangle"></i> <strong>'+title+'</strong></div><div class="alert-messages">'+message+' </div><div class="row-fluid"><a class="span6 alert-btn-cancel" ref="alertClose">Return to edit</a><a ref="confirmConfirm" class="confirmConfirm span6 alert-btn-confirm" data-action="confirm">Confirm</a></div></div></div></div><div class="alert-wrapper">').appendTo('#alerts');
        } else if (type=="loading") {
            $('<div id="a' + aid + '" class="alert-wrapper"><div class="alert alert-process"><strong><div class="loading">Loading<i class="l1">.</i><i class="l2">.</i><i class="l3">.</i></div></strong></div></div>').appendTo('#alerts');
        } else {
            $('<div id="a' + aid + '" class="alert-wrapper"><div class="alert ' +  css + ' alert-block closeable"><div class="indicator"><i class="fa ' + icon_prefix + '"></i></div><button class="close btn btn-link btn-invisible" data-action="close" ref="alertClose"><i class="fa fa-times"></i></button><span class="text"><strong>'+title+'</strong> '+message+'</span></div></div><div class="alert-wrapper">').appendTo('#alerts');
        }
    }
    if(autoclose === true) {
        setTimeout(function(){
            $('#a'+aid).remove();
        }, 5000);
    }
}

